import './App.css';
import Tile from "./Tile";
import { useState, useEffect } from 'react';

function App() {

  const gridSize = 16;
  const [counter, setCounter] = useState(0);
  const [isMax, isMaxSet] = useState(false);
  //const [gridState, setGrid] = useState(new Array(gridSize).fill(null));
  let grid = new Array(gridSize).fill(null);
  const letters = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H']
  let tileID = 1;

  function isFilled(value) {
    return value !== null;
  }

  function counterIncrease() {
    setCounter(prevValue => prevValue + 1);
  }

  function counterDecrease() {
    setCounter(prevValue => prevValue - 1);
  }

  useEffect(() => {
    console.log("counter:" + counter);
    if (counter >= 2) {
      console.log("maxed");
      isMaxSet(true);
    } else {
      console.log("not maxed");
      isMaxSet(false);
    }
  }, [counter, isMax]);

  //creating the grid positions for the letters
  for (var i = 0; i < letters.length; i++) {
    for (var j = 0; j < 2; j++) {
      let randomIndex;
      let posValue;
      let letter = letters[i];
      do {
        randomIndex = Math.floor(Math.random() * 16);
        posValue = grid[randomIndex];
      } while (isFilled(posValue));

      //setGrid(['A']);
      //setGrid(prevValue => [...prevValue.slice(0, randomIndex), letter, ...prevValue.slice(randomIndex)]);
      grid[randomIndex] = letter;
    }
  }
  console.log(grid.toString());

  return (
    <div className="App">
      <h1>Memory Game</h1>
      <div className="grid">
        {grid.map((letter, index) => {
          return <Tile key={tileID} id={tileID++} letter={letter} counter={counter} counterInc={counterIncrease} counterDec={counterDecrease} isMax={isMax}/>;
        })}
      </div>

    </div>
  );
}

export default App;
