import './App.css';
import Tile from "./Tile";
import { useState } from 'react';

function App() {

  const [counter, setCounter] = useState(0);
  const grid = new Array(16);
  const letters = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H']
  var tileID = 1;

  function isFilled(value) {
    return value !== undefined;
  }

  function counterIncrease() {
    setCounter(prevValue => prevValue + 1);
  }

  function counterDecrease() {
    setCounter(prevValue => prevValue - 1, console.log(counter));
  }

  //creating the grid positions for the letters
  for (var i = 0; i < letters.length; i++) {
    for (var j = 0; j < 2; j++) {
      let randomIndex;
      let posValue;
      do {
        randomIndex = Math.floor(Math.random() * 16);
        posValue = grid[randomIndex];
      } while (isFilled(posValue));

      grid[randomIndex] = letters[i];
    }
  }
  console.log(grid.toString());

  return (
    <div className="App">
      <h1>Memory Game</h1>
      <div className="grid">
        {grid.map((letter) => {
          return <Tile key={tileID} id={tileID++} letter={letter} counter={counter} counterInc={counterIncrease} counterDec={counterDecrease}/>;
        })}
      </div>

    </div>
  );
}

export default App;
