import './App.css';
import Tile from "./Tile";

function App() {

  const grid = new Array(16);
  const letters = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H']
  var tileID = 1;


  function handleClick() {

    if (document.getElementById(props.id).classList.contains("invisible")) {
      //if the letter was invisible...
    }
    document.getElementById(props.id).classList.toggle("invisible")
  }

  function isFilled(value) {
    return value !== undefined;
  }

  //creating the grid positions for the letters
  for (var i = 0; i < letters.length; i++) {
    for (var j = 0; j < 2; j++) {
      let randomIndex;
      let posValue;
      do {
        randomIndex = Math.floor(Math.random() * 16);
        posValue = grid[randomIndex];
      } while (isFilled(posValue));

      grid[randomIndex] = letters[i];
    }
  }
  console.log(grid.toString());

  return (
    <div className="App">
      <h1>Memory Game</h1>
      <div className="grid">
        {grid.map((letter) => {
          return <Tile key={tileID} id={tileID++} letter={letter} />;
        })}
      </div>

    </div>
  );
}

export default App;
